using UnityEngine;
using UnityEngine.SceneManagement;

public class GameManager : MonoBehaviour
{
    public static GameManager Instance { get; private set; }

    [Header("Game Settings")]
    public float gameDuration = 60f;
    public int numberOfBots = 2;

    [Header("Prefabs")]
    public GameObject playerPrefab;
    public GameObject botPrefab;

    private float timeRemaining;
    private bool gameActive = false;
    private bool gameStarted = false;

    private GameObject player;
    private GameObject[] bots;

    private int playerScore = 0;
    private int highScore = 0;

    void Awake()
    {
        if (Instance == null)
        {
            Instance = this;
        }
        else
        {
            Destroy(gameObject);
        }
    }

    void Start()
    {
        // Load high score
        highScore = PlayerPrefs.GetInt("HighScore", 0);
        
        // Wait for grid to be ready
        Invoke("StartGame", 0.5f);
    }

    void Update()
    {
        if (gameActive)
        {
            timeRemaining -= Time.deltaTime;
            
            if (timeRemaining <= 0)
            {
                timeRemaining = 0;
                EndGame();
            }
        }
    }

    public void StartGame()
    {
        gameActive = true;
        gameStarted = true;
        timeRemaining = gameDuration;

        // Reset grid
        if (GridManager.Instance != null)
        {
            GridManager.Instance.ResetGrid();
        }

        // Spawn player
        if (player == null)
        {
            SpawnPlayer();
        }
        else
        {
            player.GetComponent<PlayerController>().ResetPosition();
        }

        // Spawn bots
        if (bots == null || bots.Length != numberOfBots)
        {
            SpawnBots();
        }
        else
        {
            foreach (GameObject bot in bots)
            {
                if (bot != null)
                {
                    bot.GetComponent<BotController>().ResetPosition();
                }
            }
        }

        UIManager.Instance?.UpdateGameState(true);
    }

    void SpawnPlayer()
    {
        Vector3 spawnPos = new Vector3(0, 0.5f, 0);
        player = Instantiate(playerPrefab, spawnPos, Quaternion.identity);
    }

    void SpawnBots()
    {
        // Clean up old bots
        if (bots != null)
        {
            foreach (GameObject bot in bots)
            {
                if (bot != null)
                {
                    Destroy(bot);
                }
            }
        }

        bots = new GameObject[numberOfBots];
        TileState[] botStates = { TileState.Bot1, TileState.Bot2, TileState.Bot3 };
        Color[] botColors = { Color.red, Color.yellow, Color.green };

        for (int i = 0; i < numberOfBots; i++)
        {
            Vector3 spawnPos = new Vector3(0, 0.5f, 0);
            bots[i] = Instantiate(botPrefab, spawnPos, Quaternion.identity);
            
            BotController botController = bots[i].GetComponent<BotController>();
            botController.myTileState = botStates[i];
            
            // Set bot color
            Renderer renderer = bots[i].GetComponent<Renderer>();
            if (renderer != null)
            {
                renderer.material.color = botColors[i];
            }
        }
    }

    void EndGame()
    {
        gameActive = false;

        // Calculate scores
        CalculateScores();

        // Show end screen
        UIManager.Instance?.ShowEndScreen();
    }

    void CalculateScores()
    {
        if (GridManager.Instance == null)
            return;

        playerScore = GridManager.Instance.CountTilesByState(TileState.Player);

        // Update high score
        if (playerScore > highScore)
        {
            highScore = playerScore;
            PlayerPrefs.SetInt("HighScore", highScore);
            PlayerPrefs.Save();
        }
    }

    public bool IsGameActive()
    {
        return gameActive;
    }

    public float GetTimeRemaining()
    {
        return timeRemaining;
    }

    public int GetPlayerScore()
    {
        return playerScore;
    }

    public int GetHighScore()
    {
        return highScore;
    }

    public int GetBotScore(int botIndex)
    {
        if (GridManager.Instance == null)
            return 0;

        TileState botState = TileState.Bot1;
        if (botIndex == 0) botState = TileState.Bot1;
        else if (botIndex == 1) botState = TileState.Bot2;
        else if (botIndex == 2) botState = TileState.Bot3;

        return GridManager.Instance.CountTilesByState(botState);
    }

    public int GetTotalTiles()
    {
        if (GridManager.Instance == null)
            return 400;
        
        return GridManager.Instance.gridWidth * GridManager.Instance.gridHeight;
    }

    public void RestartGame()
    {
        StartGame();
    }

    public void LoadMainMenu()
    {
        // For now, just restart the scene
        SceneManager.LoadScene(SceneManager.GetActiveScene().buildIndex);
    }

    public int GetNumberOfBots()
    {
        return numberOfBots;
    }
}

using UnityEngine;

public class GridManager : MonoBehaviour
{
    public static GridManager Instance { get; private set; }

    [Header("Grid Settings")]
    public int gridWidth = 20;
    public int gridHeight = 20;
    public float tileSize = 1f;
    public GameObject tilePrefab;

    private Tile[,] grid;

    void Awake()
    {
        if (Instance == null)
        {
            Instance = this;
        }
        else
        {
            Destroy(gameObject);
        }
    }

    void Start()
    {
        GenerateGrid();
    }

    void GenerateGrid()
    {
        grid = new Tile[gridWidth, gridHeight];

        for (int x = 0; x < gridWidth; x++)
        {
            for (int y = 0; y < gridHeight; y++)
            {
                Vector3 position = new Vector3(x * tileSize, 0, y * tileSize);
                GameObject tileObj = Instantiate(tilePrefab, position, Quaternion.identity, transform);
                tileObj.name = $"Tile_{x}_{y}";

                Tile tile = tileObj.GetComponent<Tile>();
                tile.Initialize(x, y);
                grid[x, y] = tile;
            }
        }

        // Center camera on grid
        CenterCamera();
    }

    void CenterCamera()
    {
        float centerX = (gridWidth * tileSize) / 2f - tileSize / 2f;
        float centerZ = (gridHeight * tileSize) / 2f - tileSize / 2f;
        
        Camera mainCamera = Camera.main;
        if (mainCamera != null)
        {
            mainCamera.transform.position = new Vector3(centerX, 25f, centerZ);
            mainCamera.transform.rotation = Quaternion.Euler(90f, 0f, 0f);
        }
    }

    public Tile GetTile(int x, int y)
    {
        if (x >= 0 && x < gridWidth && y >= 0 && y < gridHeight)
        {
            return grid[x, y];
        }
        return null;
    }

    public bool IsValidPosition(int x, int y)
    {
        return x >= 0 && x < gridWidth && y >= 0 && y < gridHeight;
    }

    public int CountTilesByState(TileState state)
    {
        int count = 0;
        foreach (Tile tile in grid)
        {
            if (tile.state == state)
            {
                count++;
            }
        }
        return count;
    }

    public void ResetGrid()
    {
        foreach (Tile tile in grid)
        {
            tile.SetState(TileState.Empty);
        }
    }
}

using UnityEngine;

public class PlayerController : MonoBehaviour
{
    [Header("Movement")]
    public float moveSpeed = 5f;
    public TileState myTileState = TileState.Player;

    private int currentX, currentY;
    private bool isMoving = false;
    private Vector3 targetPosition;
    private GridManager gridManager;

    void Start()
    {
        gridManager = GridManager.Instance;
        
        // Spawn at a random position
        currentX = Random.Range(0, gridManager.gridWidth);
        currentY = Random.Range(0, gridManager.gridHeight);
        
        Vector3 spawnPos = new Vector3(currentX * gridManager.tileSize, 0.5f, currentY * gridManager.tileSize);
        transform.position = spawnPos;
        targetPosition = spawnPos;
        
        ClaimCurrentTile();
    }

    void Update()
    {
        if (!GameManager.Instance.IsGameActive())
            return;

        if (!isMoving)
        {
            HandleInput();
        }
        else
        {
            Move();
        }
    }

    void HandleInput()
    {
        int newX = currentX;
        int newY = currentY;

        if (Input.GetKeyDown(KeyCode.W) || Input.GetKeyDown(KeyCode.UpArrow))
        {
            newY++;
        }
        else if (Input.GetKeyDown(KeyCode.S) || Input.GetKeyDown(KeyCode.DownArrow))
        {
            newY--;
        }
        else if (Input.GetKeyDown(KeyCode.A) || Input.GetKeyDown(KeyCode.LeftArrow))
        {
            newX--;
        }
        else if (Input.GetKeyDown(KeyCode.D) || Input.GetKeyDown(KeyCode.RightArrow))
        {
            newX++;
        }

        if (newX != currentX || newY != currentY)
        {
            TryMove(newX, newY);
        }
    }

    void TryMove(int newX, int newY)
    {
        if (!gridManager.IsValidPosition(newX, newY))
            return;

        Tile targetTile = gridManager.GetTile(newX, newY);
        
        if (targetTile != null && targetTile.CanWalkOn(myTileState))
        {
            currentX = newX;
            currentY = newY;
            targetPosition = new Vector3(currentX * gridManager.tileSize, 0.5f, currentY * gridManager.tileSize);
            isMoving = true;
            
            ClaimCurrentTile();
        }
    }

    void Move()
    {
        transform.position = Vector3.MoveTowards(transform.position, targetPosition, moveSpeed * Time.deltaTime);
        
        if (Vector3.Distance(transform.position, targetPosition) < 0.01f)
        {
            transform.position = targetPosition;
            isMoving = false;
        }
    }

    void ClaimCurrentTile()
    {
        Tile currentTile = gridManager.GetTile(currentX, currentY);
        if (currentTile != null && currentTile.CanBeClaimedBy(myTileState))
        {
            currentTile.SetState(myTileState);
        }
    }

    public void ResetPosition()
    {
        currentX = Random.Range(0, gridManager.gridWidth);
        currentY = Random.Range(0, gridManager.gridHeight);
        
        Vector3 spawnPos = new Vector3(currentX * gridManager.tileSize, 0.5f, currentY * gridManager.tileSize);
        transform.position = spawnPos;
        targetPosition = spawnPos;
        isMoving = false;
        
        ClaimCurrentTile();
    }
}

