using UnityEngine;
using UnityEditor;

public class SetupJoystick
{
    [MenuItem("Tools/Setup Joystick References")]
    public static void Execute()
    {
        GameObject joystickArea = GameObject.Find("Canvas/JoystickArea");
        if (joystickArea == null)
        {
            Debug.LogError("JoystickArea not found!");
            return;
        }

        VirtualJoystick joystick = joystickArea.GetComponent<VirtualJoystick>();
        if (joystick == null)
        {
            Debug.LogError("VirtualJoystick component not found!");
            return;
        }

        GameObject background = GameObject.Find("Canvas/JoystickArea/JoystickBackground");
        GameObject handle = GameObject.Find("Canvas/JoystickArea/JoystickBackground/JoystickHandle");

        if (background == null || handle == null)
        {
            Debug.LogError("Joystick UI elements not found!");
            return;
        }

        SerializedObject so = new SerializedObject(joystick);
        so.FindProperty("joystickBackground").objectReferenceValue = background.GetComponent<RectTransform>();
        so.FindProperty("joystickHandle").objectReferenceValue = handle.GetComponent<RectTransform>();
        so.FindProperty("handleRange").floatValue = 50f;
        so.ApplyModifiedProperties();

        EditorUtility.SetDirty(joystick);

        Debug.Log("Joystick references set successfully!");
    }
}

using UnityEngine;

public class GameSettings : MonoBehaviour
{
    public static GameSettings Instance { get; private set; }

    public int mapSize = 20;

    void Awake()
    {
        if (Instance == null)
        {
            Instance = this;
            DontDestroyOnLoad(gameObject);
        }
        else
        {
            Destroy(gameObject);
        }
    }

    public void SetMapSize(int size)
    {
        mapSize = size;
        Debug.Log("Map size set to: " + size + "x" + size);
    }

    public int GetMapSize()
    {
        return mapSize;
    }
}

using UnityEngine;
using UnityEngine.UI;
using UnityEngine.SceneManagement;
using System.Collections;

public class LoadingManager : MonoBehaviour
{
    [Header("UI Elements")]
    public Slider loadingBar;
    public Text loadingText;

    [Header("Settings")]
    public float loadingDuration = 2f;
    public string nextSceneName = "Home";

    void Start()
    {
        StartCoroutine(LoadingSequence());
    }

    IEnumerator LoadingSequence()
    {
        float elapsed = 0f;

        while (elapsed < loadingDuration)
        {
            elapsed += Time.deltaTime;
            float progress = Mathf.Clamp01(elapsed / loadingDuration);

            // Update UI
            if (loadingBar != null)
            {
                loadingBar.value = progress;
            }

            if (loadingText != null)
            {
                loadingText.text = "Loading... " + Mathf.RoundToInt(progress * 100f) + "%";
            }

            yield return null;
        }

        // Ensure we reach 100%
        if (loadingBar != null)
        {
            loadingBar.value = 1f;
        }
        if (loadingText != null)
        {
            loadingText.text = "Loading... 100%";
        }

        yield return new WaitForSeconds(0.5f);

        // Load next scene
        SceneManager.LoadScene(nextSceneName);
    }
}

using UnityEngine;
using UnityEngine.SceneManagement;
using UnityEngine.UI;

public class MenuManager : MonoBehaviour
{
    [Header("Map Size Buttons")]
    public Button button10x10;
    public Button button15x15;
    public Button button20x20;

    void Start()
    {
        // Add button listeners
        if (button10x10 != null)
        {
            button10x10.onClick.AddListener(() => SelectMapSize(10));
        }
        if (button15x15 != null)
        {
            button15x15.onClick.AddListener(() => SelectMapSize(15));
        }
        if (button20x20 != null)
        {
            button20x20.onClick.AddListener(() => SelectMapSize(20));
        }
    }

    void SelectMapSize(int size)
    {
        // Create GameSettings if it doesn't exist
        GameSettings settings = FindObjectOfType<GameSettings>();
        if (settings == null)
        {
            GameObject settingsObj = new GameObject("GameSettings");
            settings = settingsObj.AddComponent<GameSettings>();
        }

        settings.SetMapSize(size);

        // Load Game scene
        SceneManager.LoadScene("Game");
    }
}

