using UnityEditor;
using System.Collections.Generic;

public class ConfigureBuildSettings
{
    [MenuItem("Tools/Configure Build Settings")]
    public static void Execute()
    {
        List<EditorBuildSettingsScene> editorBuildSettingsScenes = new List<EditorBuildSettingsScene>();

        // Add scenes in correct order
        editorBuildSettingsScenes.Add(new EditorBuildSettingsScene("Assets/Scenes/Loading.unity", true));
        editorBuildSettingsScenes.Add(new EditorBuildSettingsScene("Assets/Scenes/Home.unity", true));
        editorBuildSettingsScenes.Add(new EditorBuildSettingsScene("Assets/Scenes/Game.unity", true));

        EditorBuildSettings.scenes = editorBuildSettingsScenes.ToArray();

        UnityEngine.Debug.Log("Build settings configured! Scenes order: Loading (0), Home (1), Game (2)");
    }
}

using UnityEngine;
using UnityEditor;
using UnityEngine.UI;

public class FixJoystickAndCanvas
{
    [MenuItem("Tools/Fix Joystick And Canvas Settings")]
    public static void Execute()
    {
        // Fix Joystick references
        GameObject joystickArea = GameObject.Find("Canvas/JoystickArea");
        if (joystickArea != null)
        {
            VirtualJoystick joystick = joystickArea.GetComponent<VirtualJoystick>();
            if (joystick != null)
            {
                GameObject background = GameObject.Find("Canvas/JoystickArea/JoystickBackground");
                GameObject handle = GameObject.Find("Canvas/JoystickArea/JoystickBackground/JoystickHandle");

                if (background != null && handle != null)
                {
                    SerializedObject so = new SerializedObject(joystick);
                    so.FindProperty("joystickBackground").objectReferenceValue = background.GetComponent<RectTransform>();
                    so.FindProperty("joystickHandle").objectReferenceValue = handle.GetComponent<RectTransform>();
                    so.FindProperty("handleRange").floatValue = 50f;
                    so.ApplyModifiedProperties();
                    EditorUtility.SetDirty(joystick);
                    Debug.Log("Joystick references fixed!");
                }
            }
        }

        // Fix all Canvas Scalers in current scene
        Canvas[] canvases = GameObject.FindObjectsOfType<Canvas>();
        foreach (Canvas canvas in canvases)
        {
            CanvasScaler scaler = canvas.GetComponent<CanvasScaler>();
            if (scaler == null)
            {
                scaler = canvas.gameObject.AddComponent<CanvasScaler>();
            }

            scaler.uiScaleMode = CanvasScaler.ScaleMode.ScaleWithScreenSize;
            scaler.referenceResolution = new Vector2(1080, 1920);
            scaler.matchWidthOrHeight = 0f;
            scaler.referencePixelsPerUnit = 100f;

            EditorUtility.SetDirty(scaler);
            Debug.Log("Fixed Canvas Scaler for: " + canvas.name);
        }

        Debug.Log("All fixes applied!");
    }
}

Shader "UI/ScrollingTexture"
{
    Properties
    {
        [PerRendererData] _MainTex ("Sprite Texture", 2D) = "white" {}
        _Color ("Tint", Color) = (1,1,1,1)

        [Header(Scrolling Settings)]
        _ScrollSpeedX ("Scroll Speed X", Float) = 0.5
        _ScrollSpeedY ("Scroll Speed Y", Float) = 0
        _TilingX ("Tiling X", Float) = 1
        _TilingY ("Tiling Y", Float) = 1

        [Header(UI Settings)]
        _StencilComp ("Stencil Comparison", Float) = 8
        _Stencil ("Stencil ID", Float) = 0
        _StencilOp ("Stencil Operation", Float) = 0
        _StencilWriteMask ("Stencil Write Mask", Float) = 255
        _StencilReadMask ("Stencil Read Mask", Float) = 255

        _ColorMask ("Color Mask", Float) = 15

        [Toggle(UNITY_UI_ALPHACLIP)] _UseUIAlphaClip ("Use Alpha Clip", Float) = 0
    }

    SubShader
    {
        Tags
        {
            "Queue"="Transparent"
            "IgnoreProjector"="True"
            "RenderType"="Transparent"
            "PreviewType"="Plane"
            "CanUseSpriteAtlas"="True"
        }

        Stencil
        {
            Ref [_Stencil]
            Comp [_StencilComp]
            Pass [_StencilOp]
            ReadMask [_StencilReadMask]
            WriteMask [_StencilWriteMask]
        }

        Cull Off
        Lighting Off
        ZWrite Off
        ZTest [unity_GUIZTestMode]
        Blend SrcAlpha OneMinusSrcAlpha
        ColorMask [_ColorMask]

        Pass
        {
            Name "Default"

            CGPROGRAM
            #pragma vertex vert
            #pragma fragment frag
            #pragma target 2.0

            #include "UnityCG.cginc"
            #include "UnityUI.cginc"

            #pragma multi_compile_local _ UNITY_UI_CLIP_RECT
            #pragma multi_compile_local _ UNITY_UI_ALPHACLIP

            struct appdata_t
            {
                float4 vertex   : POSITION;
                float4 color    : COLOR;
                float2 texcoord : TEXCOORD0;
                UNITY_VERTEX_INPUT_INSTANCE_ID
            };

            struct v2f
            {
                float4 vertex   : SV_POSITION;
                fixed4 color    : COLOR;
                float2 texcoord  : TEXCOORD0;
                float4 worldPosition : TEXCOORD1;
                UNITY_VERTEX_OUTPUT_STEREO
            };

            sampler2D _MainTex;
            fixed4 _Color;
            fixed4 _TextureSampleAdd;
            float4 _ClipRect;
            float4 _MainTex_ST;
            float _ScrollSpeedX;
            float _ScrollSpeedY;
            float _TilingX;
            float _TilingY;

            v2f vert(appdata_t v)
            {
                v2f OUT;
                UNITY_SETUP_INSTANCE_ID(v);
                UNITY_INITIALIZE_VERTEX_OUTPUT_STEREO(OUT);
                OUT.worldPosition = v.vertex;
                OUT.vertex = UnityObjectToClipPos(OUT.worldPosition);

                OUT.texcoord = TRANSFORM_TEX(v.texcoord, _MainTex);
                OUT.color = v.color * _Color;
                return OUT;
            }

            fixed4 frag(v2f IN) : SV_Target
            {
                // Apply tiling
                float2 tiledUV = IN.texcoord * float2(_TilingX, _TilingY);

                // Apply scrolling offset based on time
                float2 offset = float2(_ScrollSpeedX, _ScrollSpeedY) * _Time.y;
                float2 scrolledUV = tiledUV + offset;

                // Sample texture with scrolled UVs
                half4 color = (tex2D(_MainTex, scrolledUV) + _TextureSampleAdd) * IN.color;

                #ifdef UNITY_UI_CLIP_RECT
                color.a *= UnityGet2DClipping(IN.worldPosition.xy, _ClipRect);
                #endif

                #ifdef UNITY_UI_ALPHACLIP
                clip (color.a - 0.001);
                #endif

                return color;
            }
            ENDCG
        }
    }
}

