using UnityEngine;

[RequireComponent(typeof(Animator))]
public class AnimatedCharacter : MonoBehaviour
{
    [Header("Animation Settings")]
    public float rotationSpeed = 720f; // Degrees per second (fast rotation)
    public float idleThreshold = 0.1f; // Speed below which character is considered idle
    public float animationBlendSpeed = 5f;

    private Animator animator;
    private Vector3 lastPosition;
    private Vector3 currentVelocity;

    // Animation parameter names
    private static readonly int IsWalkingHash = Animator.StringToHash("IsWalking");
    private static readonly int SpeedHash = Animator.StringToHash("Speed");

    void Awake()
    {
        animator = GetComponent<Animator>();
        lastPosition = transform.position;
    }

    void Update()
    {
        UpdateMovementAnimation();
        UpdateRotation();
    }

    void UpdateMovementAnimation()
    {
        // Calculate current velocity
        currentVelocity = (transform.position - lastPosition) / Time.deltaTime;
        lastPosition = transform.position;

        float speed = new Vector3(currentVelocity.x, 0, currentVelocity.z).magnitude;

        // Update animator based on speed
        if (animator != null)
        {
            // Try both common parameter names
            bool isWalking = speed > idleThreshold;

            // Set IsWalking parameter if it exists
            if (HasParameter(IsWalkingHash))
            {
                animator.SetBool(IsWalkingHash, isWalking);
            }

            // Set Speed parameter if it exists (for blend trees)
            if (HasParameter(SpeedHash))
            {
                float targetSpeed = isWalking ? 1f : 0f;
                float currentSpeed = animator.GetFloat(SpeedHash);
                float blendedSpeed = Mathf.Lerp(currentSpeed, targetSpeed, animationBlendSpeed * Time.deltaTime);
                animator.SetFloat(SpeedHash, blendedSpeed);
            }
        }
    }

    void UpdateRotation()
    {
        // Only rotate if moving
        Vector3 horizontalVelocity = new Vector3(currentVelocity.x, 0, currentVelocity.z);

        if (horizontalVelocity.magnitude > idleThreshold)
        {
            Quaternion targetRotation = Quaternion.LookRotation(horizontalVelocity);
            transform.rotation = Quaternion.RotateTowards(
                transform.rotation, 
                targetRotation, 
                rotationSpeed * Time.deltaTime
            );
        }
    }

    bool HasParameter(int paramHash)
    {
        if (animator == null) return false;

        foreach (AnimatorControllerParameter param in animator.parameters)
        {
            if (param.nameHash == paramHash)
            {
                return true;
            }
        }
        return false;
    }

    // Public method to manually set animation state
    public void SetWalking(bool walking)
    {
        if (animator != null && HasParameter(IsWalkingHash))
        {
            animator.SetBool(IsWalkingHash, walking);
        }
    }
}

