using UnityEngine;
using UnityEditor;
using UnityEngine.UI;

public class WireSettingsButton
{
    public static void Execute()
    {
        GameObject settingsButton = GameObject.Find("Canvas/SettingsButton");
        GameObject settingsPopup = GameObject.Find("Canvas/SettingsPopup");

        if (settingsButton != null && settingsPopup != null)
        {
            var button = settingsButton.GetComponent<Button>();
            var popup = settingsPopup.GetComponent<SettingsPopup>();

            if (button != null && popup != null)
            {
                button.onClick.RemoveAllListeners();
                button.onClick.AddListener(() => {
                    // Play button click sound
                    if (SoundManager.Instance != null)
                    {
                        SoundManager.Instance.PlayButtonClick();
                    }
                    popup.OpenPopup();
                });

                Debug.Log("Settings button wired with sound!");
            }
        }
    }
}

using UnityEngine;
using UnityEngine.UI;

public class SettingsPopup : MonoBehaviour
{
    [Header("UI References")]
    public Button musicButton;
    public Button sfxButton;
    public Button closeButton;

    [Header("Music Sprites")]
    public Sprite musicOnSprite;
    public Sprite musicOffSprite;

    [Header("SFX Sprites")]
    public Sprite sfxOnSprite;
    public Sprite sfxOffSprite;

    private Image musicButtonImage;
    private Image sfxButtonImage;

    void Start()
    {
        // Get button images
        if (musicButton != null)
        {
            musicButtonImage = musicButton.GetComponent<Image>();
            musicButton.onClick.AddListener(OnMusicButtonClicked);
        }

        if (sfxButton != null)
        {
            sfxButtonImage = sfxButton.GetComponent<Image>();
            sfxButton.onClick.AddListener(OnSFXButtonClicked);
        }

        // Setup close button
        if (closeButton != null)
        {
            closeButton.onClick.AddListener(ClosePopup);
        }

        // Update button visuals
        UpdateButtonVisuals();
    }

    void OnMusicButtonClicked()
    {
        if (SoundManager.Instance != null)
        {
            SoundManager.Instance.PlayButtonClick();
            SoundManager.Instance.ToggleMusic();
            UpdateButtonVisuals();
        }
    }

    void OnSFXButtonClicked()
    {
        if (SoundManager.Instance != null)
        {
            SoundManager.Instance.PlayButtonClick();
            SoundManager.Instance.ToggleSFX();
            UpdateButtonVisuals();
        }
    }

    void UpdateButtonVisuals()
    {
        if (SoundManager.Instance == null) return;

        // Update music button sprite
        if (musicButtonImage != null)
        {
            musicButtonImage.sprite = SoundManager.Instance.IsMusicEnabled() ? musicOnSprite : musicOffSprite;
        }

        // Update SFX button sprite
        if (sfxButtonImage != null)
        {
            sfxButtonImage.sprite = SoundManager.Instance.IsSFXEnabled() ? sfxOnSprite : sfxOffSprite;
        }
    }

    public void ClosePopup()
    {
        // Play button click sound
        if (SoundManager.Instance != null)
        {
            SoundManager.Instance.PlayButtonClick();
        }

        gameObject.SetActive(false);
    }

    public void OpenPopup()
    {
        // Play button click sound
        if (SoundManager.Instance != null)
        {
            SoundManager.Instance.PlayButtonClick();
        }

        gameObject.SetActive(true);

        // Refresh button visuals
        UpdateButtonVisuals();
    }
}

using UnityEngine;
using UnityEditor;
using UnityEngine.UI;

public class SetupSettingsPopupWithSprites
